import Player from "./components/Player";
import Header from "./components/Header";

import ReactDOM from "react-dom/client";
import data from "./data";
import React from "react";

export default function App(props) {
  let [playerPressed, setPlayerPressed] = React.useState(null);
  function handleButtonClick(playerName, playerId) {
    setPlayerPressed(playerName);
  }
  function determineHigherScore(player1id, player2id) {
    const player1withid = data.find((player) => player.id === player1id);
    const player2withid = data.find((player) => player.id === player2id);
    if (player1withid.points > player2withid.points) {
      return player1id;
    }
    return player2id;
  }

  const values = Array.from({ length: 3 }, (_, i) => i);
  let value1 = values.splice(Math.floor(Math.random * values.length), 1)[0];
  let value2 = values[Math.floor(Math.random() * values.length)];

  const entryElements = data.map((entry) => {
    return (
      <Player
        key={entry.id}
        imgSrc={entry.img.src}
        imgAlt={entry.img.alt}
        title={entry.title}
        onButtonClick={handleButtonClick}
      />
    );
  });
  console.log(entryElements);
  return (
    <div className="app">
      <h1>{playerPressed}</h1>
      <Header />
      <div className="content">
        {value1}
        {value2}
        {/* <main className="player1">{entryElements[value1]}</main>
        <main className="player2">{entryElements[value2]}</main> */}
      </div>
    </div>
  );
}

