import Player from "./components/Player";
import Header from "./components/Header";
import ReactDOM from "react-dom/client";
import data from "./data";
import React from "react";

export default function App(props) {
  let [playerPressed, setPlayerPressed] = React.useState(null);
  let [score, setScore] = React.useState(0);

  function increaseScore() {
    setScore((prevScore) => prevScore + 1);
  }
  function resetScore() {
    setScore(0);
  }
  function handleButtonClick(playerId) {
    setPlayerPressed(playerId);
  }
  const [value1, setValue1] = React.useState(null);
  const [value2, setValue2] = React.useState(null);

  React.useEffect(() => {
    // Only set value1 and value2 once when the component mounts
    const array = Array.from({ length: 6 }, (_, i) => i);
    const tempValue1 = array.splice(
      Math.floor(Math.random() * array.length),
      1
    )[0];
    const tempValue2 = array[Math.floor(Math.random() * array.length)];
    setValue1(tempValue1);
    setValue2(tempValue2);
  }, []);

  // function determineHigherScore(chosenPlayerId, player1Id, player2Id) {
  //   if (chosenPlayerId === null) {
  //     return 3;
  //   }
  //   const player1withid = data.find((player) => player.id === player1Id);
  //   const player2withid = data.find((player) => player.id === player2Id);
  //   if (
  //     player1withid.points > player2withid.points &&
  //     player1Id == chosenPlayerId
  //   ) {
  //     return 1;
  //   }
  //   if (
  //     player1withid.points < player2withid.points &&
  //     player2Id == chosenPlayerId
  //   ) {
  //     return 1;
  //   }
  //   return 2;
  // }

  React.useEffect(() => {
    if (
      value1 !== null &&
      value2 !== null &&
      data[value1].points > data[value2].points
    ) {
      increaseScore();
    } else {
      resetScore();
    }
  });
  const entryElements = data.map((entry) => {
    return (
      <Player
        key={entry.id}
        id={entry.id}
        imgSrc={entry.img.src}
        imgAlt={entry.img.alt}
        title={entry.title}
        onButtonClick={handleButtonClick}
      />
    );
  });
  console.log(entryElements);
  return (
    <div className="app">
      <h1>{playerPressed}</h1>
      {score}
      <Header />
      {value1 !== null && value2 !== null ? (
        <div className="content">
          <main className="player1">
            {playerPressed ? (
              `You chose ${data[playerPressed - 1].title}. ${
                playerPressed - 1 === value1
                  ? data[value1].points > data[value2].points
                    ? `Correct ${data[value1].points}`
                    : `Wrong ${data[value1].points}`
                  : data[value2].points > data[value1].points
                  ? `Correct ${data[value2].points}`
                  : `Wrong ${data[value2].points}`
              }`
            ) : (
              <div>
                {entryElements[value1]}
                <h3>{data[value1].points}</h3>
              </div>
            )}
          </main>
          <main className="player2">
            {value1 !== null && value2 !== null && playerPressed ? (
              ""
            ) : (
              <div>
                {entryElements[value2]}
                <h3>{data[value2].points}</h3>
              </div>
            )}
          </main>

          {/* <main>determineHigherScore & </main> */}
        </div>
      ) : (
        ""
      )}
    </div>
  );
}

