import Player from "./components/Player";
import Header from "./components/Header";
import ReactDOM from "react-dom/client";
import data from "./data";
import React from "react";

export default function App(props) {
  const [playerPressed, setPlayerPressed] = React.useState(null);
  const [score, setScore] = React.useState(0);
  const [resetTrigger, setResetTrigger] = React.useState(true);
  const [isCorrect, setIsCorrect] = React.useState(null);
  const [count, setCount] = React.useState(0);
  const [players, setPlayers] = React.useState(0); // State to track input
  const [isStartScreen, setIsStartScreen] = React.useState(true);

  const increment = () => {
    setCount((prevCount) => prevCount + 1);
  };

  const handleInputChange = (e) => {
    setPlayers(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (players.trim()) {
      setIsStartScreen(false);
    }
    setDiscoveredList(Array.from({ length: 50 }, () => false));
  };
  function handleResetTrigger() {
    setResetTrigger((currentMode) => !currentMode);
  }
  function increaseScore() {
    setScore((prevScore) => prevScore + 1);
  }
  function resetScore() {
    setScore(0);
  }
  function handleButtonClick(playerId) {
    setPlayerPressed(playerId);
  }
  function PlayerContainer({ value }) {
    if (value === null) return null;

    return <>{entryElements[value]}</>;
  }
  const [value1, setValue1] = React.useState(null);
  const [value2, setValue2] = React.useState(null);
  const [dataSet, setDataSet] = React.useState([]);
  const [entryElements, setEntryElements] = React.useState([]);
  const [discoveredList, setDiscoveredList] = React.useState([]);

  const toggleValueAtIndex = (index) => {
    setDiscoveredList((prevList) => {
      // Create a copy of the current list
      const newList = [...prevList];

      // Toggle the value at the specified index
      newList[index] = !newList[index];

      // Return the updated list
      return newList;
    });
  };
  React.useEffect(() => {
    if (data) {
      const elements = data.map((entry) => (
        <Player
          key={entry.id}
          id={entry.id}
          imgSrc={entry.img.src}
          imgAlt={entry.img.alt}
          title={entry.title}
          points={entry.points}
          onButtonClick={handleButtonClick}
          showPoints={discoveredList[entry.id]}
        />
      ));
      setEntryElements(elements); // Set the entryElements state
    }
  }, [data, discoveredList]); // Dependencies are data and discoveredList

  React.useEffect(() => {
    if (resetTrigger) {
      // Perform your logic like setting value1 and value2
      const array = Array.from({ length: Number(players) }, (_, i) => i);
      let tempValue1 = null;
      if (count != 0) {
        toggleValueAtIndex(value2 + 1);
        tempValue1 = value2;
      } else {
        tempValue1 = array.splice(
          Math.floor(Math.random() * array.length),
          1
        )[0];
      }
      const tempValue2 = array[Math.floor(Math.random() * array.length)];
      increment();
      setValue1(tempValue1);
      setValue2(tempValue2);
      setDataSet([data[value1], data[value2]]);

      // Handling the score logic

      // Only set resetTrigger to false after everything is done
      setResetTrigger(false); // This will prevent infinite looping
    }
  }, [resetTrigger]);

  React.useEffect(() => {
    if (playerPressed === null || value1 === null || value2 === null) {
      return;
    }
    const correctPlayerId = playerPressed - 1;
    const chosenPlayerId =
      data[value1].points > data[value2].points ? value1 : value2;
    if (chosenPlayerId === correctPlayerId) {
      setIsCorrect(true);
      increaseScore();
    } else {
      setIsCorrect(false);
      resetScore();
    }
    const timeout = setTimeout(() => {
      setResetTrigger(true);
      setPlayerPressed(null);
    }, 1000);
  }, [playerPressed, value1, value2]);

  // function determineHigherScore(chosenPlayerId, player1Id, player2Id) {
  //   if (chosenPlayerId === null) {
  //     return 3;
  //   }
  //   const player1withid = data.find((player) => player.id === player1Id);
  //   const player2withid = data.find((player) => player.id === player2Id);
  //   if (
  //     player1withid.points > player2withid.points &&
  //     player1Id == chosenPlayerId
  //   ) {
  //     return 1;
  //   }
  //   if (
  //     player1withid.points < player2withid.points &&
  //     player2Id == chosenPlayerId
  //   ) {
  //     return 1;
  //   }
  //   return 2;
  // }

  // React.useEffect(() => {
  //   if (
  //     value1 !== null &&
  //     value2 !== null &&
  //     data[value1].points > data[value2].points
  //   ) {
  //     increaseScore();
  //   } else {
  //     resetScore();
  //   }
  // }, []);

  // const entryElements = data
  //   ? data.map((entry) => (
  //       <Player
  //         key={entry.id}
  //         id={entry.id}
  //         imgSrc={entry.img.src}
  //         imgAlt={entry.img.alt}
  //         title={entry.title}
  //         points={entry.points}
  //         onButtonClick={handleButtonClick}
  //         showPoints={discoveredList[entry.id]}
  //       />
  //     ))
  //   : null;
  console.log(entryElements);
  return (
    <div className="app">
      {isStartScreen ? (
        <div className="startScreen">
          <h1>NBA Higher or Lower</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              id="startTextBox"
              placeholder="Enter number of players"
              value={players}
              onChange={handleInputChange}
            />
          </form>
        </div>
      ) : (
        <>
          <h1>{playerPressed}</h1>
          {score}
          <Header />
          {typeof Number(players)}
          {value1 !== null && value2 !== null ? (
            <div className="content">
              <main className="player1">
                {playerPressed ? (
                  `You chose ${data[playerPressed - 1].title}. ${
                    isCorrect ? "Correct" : "Wrong"
                  }`
                ) : (
                  <div>
                    {/* {entryElements[value1]} */}
                    <PlayerContainer value={value1} />
                  </div>
                )}
              </main>
              <main className="player2">
                {value1 !== null && value2 !== null && playerPressed ? (
                  ""
                ) : (
                  <div>
                    <PlayerContainer value={value2} />
                  </div>
                )}
              </main>
            </div>
          ) : (
            ""
          )}
        </>
      )}
    </div>
  );

  // Log to console
  console.log("Hello console");
}
